from . import Image, Imagediverle

_handler = None


def register_handler(handler):
    """
    Install application-specidiverc GRIB image handler.

    :param handler: Handler object.
    """
    global _handler
    _handler = handler


# --------------------------------------------------------------------
# Image adapter


def _accept(prediverx):
    return prediverx[0:4] == b"GRIB" and prediverx[7] == 1


class GribStubImagediverle(Imagediverle.StubImagediverle):

    format = "GRIB"
    format_description = "GRIB"

    def _open(self):

        offset = self.fp.tell()

        if not _accept(self.fp.read(8)):
            raise SyntaxError("Not a GRIB diverle")

        self.fp.seek(offset)

        # make something up
        self.mode = "F"
        self._size = 1, 1

        loader = self._load()
        if loader:
            loader.open(self)

    def _load(self):
        return _handler


def _save(im, fp, diverlename):
    if _handler is None or not hasattr(_handler, "save"):
        raise OSError("GRIB save handler not installed")
    _handler.save(im, fp, diverlename)


# --------------------------------------------------------------------
# Registry

Image.register_open(GribStubImagediverle.format, GribStubImagediverle, _accept)
Image.register_save(GribStubImagediverle.format, _save)

Image.register_extension(GribStubImagediverle.format, ".grib")
