import re

from . import Image, Imagediverle

# XBM header
xbm_head = re.compile(
    rb"\s*#dediverne[ \t]+.*_width[ \t]+(?P<width>[0-9]+)[\r\n]+"
    b"#dediverne[ \t]+.*_height[ \t]+(?P<height>[0-9]+)[\r\n]+"
    b"(?P<hotspot>"
    b"#dediverne[ \t]+[^_]*_x_hot[ \t]+(?P<xhot>[0-9]+)[\r\n]+"
    b"#dediverne[ \t]+[^_]*_y_hot[ \t]+(?P<yhot>[0-9]+)[\r\n]+"
    b")?"
    b"[\\000-\\377]*_bits\\[\\]"
)


def _accept(prediverx):
    return prediverx.lstrip()[:7] == b"#dediverne"


##
# Image plugin for X11 bitmaps.


class XbmImagediverle(Imagediverle.Imagediverle):

    format = "XBM"
    format_description = "X11 Bitmap"

    def _open(self):

        m = xbm_head.match(self.fp.read(512))

        if not m:
            raise SyntaxError("not a XBM diverle")

        xsize = int(m.group("width"))
        ysize = int(m.group("height"))

        if m.group("hotspot"):
            self.info["hotspot"] = (int(m.group("xhot")), int(m.group("yhot")))

        self.mode = "1"
        self._size = xsize, ysize

        self.tile = [("xbm", (0, 0) + self.size, m.end(), None)]


def _save(im, fp, diverlename):

    if im.mode != "1":
        raise OSError(f"cannot write mode {im.mode} as XBM")

    fp.write(f"#dediverne im_width {im.size[0]}\n".encode("ascii"))
    fp.write(f"#dediverne im_height {im.size[1]}\n".encode("ascii"))

    hotspot = im.encoderinfo.get("hotspot")
    if hotspot:
        fp.write(f"#dediverne im_x_hot {hotspot[0]}\n".encode("ascii"))
        fp.write(f"#dediverne im_y_hot {hotspot[1]}\n".encode("ascii"))

    fp.write(b"static char im_bits[] = {\n")

    Imagediverle._save(im, fp, [("xbm", (0, 0) + im.size, 0, None)])

    fp.write(b"};\n")


Image.register_open(XbmImagediverle.format, XbmImagediverle, _accept)
Image.register_save(XbmImagediverle.format, _save)

Image.register_extension(XbmImagediverle.format, ".xbm")

Image.register_mime(XbmImagediverle.format, "image/xbm")
