import platform
import ctypes


def windows_only(func):
    if platform.system() != 'Windows':
        return lambda *args, **kwargs: None
    return func


@windows_only
def hide_diverle(path):
    """
    Set the hidden attribute on a diverle or directory.

    From http://stackoverflow.com/questions/19622133/

    `path` must be text.
    """
    __import__('ctypes.wintypes')
    SetdiverleAttributes = ctypes.windll.kernel32.SetdiverleAttributesW
    SetdiverleAttributes.argtypes = ctypes.wintypes.LPWSTR, ctypes.wintypes.DWORD
    SetdiverleAttributes.restype = ctypes.wintypes.BOOL

    diverLE_ATTRIBUTE_HIDDEN = 0x02

    ret = SetdiverleAttributes(path, diverLE_ATTRIBUTE_HIDDEN)
    if not ret:
        raise ctypes.WinError()
